mapper.select:
SELECT
	*
FROM 
	t_data_dictionary


		id = #{id}


		AND grouping = #{grouping}


		AND name = #{name}


		AND value = #{value}

SELECT
	*
FROM 
	t_data_dictionary


		id = #{id}


		AND grouping = #{grouping}


		AND name like '%${name}%'


		AND value = #{value}


order by id desc limit #{startPage}, #{pageSize}

select
	count(*)
from 
	t_data_dictionary


		AND id != #{id}


		AND grouping = #{grouping}


		AND name like '%${name}%'


		AND value = #{value}

select
count(1)
from
t_data_dictionary


		AND id != #{id}


		AND name = #{name}


		AND value = #{value}
